import React from "react";
import { Link } from "react-router-dom";
import { Row, Col, Card, ProgressBar } from "react-bootstrap";
import { connect } from "react-redux";
import { fetchProducts } from "../../redux/actions/product-actions/fetchProductsAction";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import Slider from "react-slick";

// custom prev arrow
function SamplePrevArrow(props) {
  const { onClick } = props;
  return <i onClick={onClick} className='fa fa-arrow-left' aria-hidden='true'></i>;
}

// custom next arrow
function SampleNextArrow(props) {
  const { onClick } = props;
  return <i onClick={onClick} className='fa fa-arrow-right' aria-hidden='true'></i>;
}

class ProductsCarousel extends React.Component {
  // will fetch the products before the compnent mounts
  componentDidMount() {
    this.props.fetchP();
  }

  render() {
    let productsNumber = this.props.productsNumber;
    const settings = {
      dots: true,
      infinite: false,
      speed: 700,
      slidesToShow: productsNumber,
      slidesToScroll: 4,
      arrows: true,
      prevArrow: <SamplePrevArrow />,
      nextArrow: <SampleNextArrow />,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <div className='featured-products'>
        <h2>{this.props.title}</h2>
        {this.props.products.loading && <ProgressBar animated now={100} />}
        <Slider {...settings}>
          {!this.props.products.loading &&
            this.props.products.products.map(product => {
              return (
                <Col key={product._id} className='product-card'>
                  <Card>
                    <Link to={`/product/${product._id}`}>
                      <Card.Img
                        className='product-card-image'
                        variant='top'
                        src={product.productImage[0]}
                      />
                    </Link>

                    <Card.Body className='product-details'>
                      <Card.Title className='product-name'>
                        <Link to={`/product/${product._id}`}>{product.name}</Link>
                      </Card.Title>

                      <Row>
                        <Col>
                          <Card.Text className='product-price'>
                            {"$" + product.price}
                          </Card.Text>
                        </Col>
                        <Col>
                          <Card.Text>
                            <i className='fa fa-star' aria-hidden='true' />
                            <i className='fa fa-star' aria-hidden='true' />
                            <i className='fa fa-star' aria-hidden='true' />
                            <i className='fa fa-star-half-o' aria-hidden='true' />
                            <i className='fa fa-star-o' aria-hidden='true' />
                          </Card.Text>
                        </Col>
                      </Row>
                    </Card.Body>
                  </Card>
                </Col>
              );
            })}
        </Slider>
      </div>
    );
  }
}

function mapStateToProps(state) {
  const { productsss } = state;
  return { products: productsss };
}

function mapDispatchToProps(dispatch) {
  return { fetchP: () => dispatch(fetchProducts()) };
}
export default connect(mapStateToProps, mapDispatchToProps)(ProductsCarousel);
